import logo from "./logo.svg";
import "./App.css";
import { Box, Button, Modal, Typography } from "@mui/material";
import { useState } from "react";
import { ThemeProvider, createTheme } from "@mui/material/styles";

function App() {
  const [modalOpen, setModalOpen] = useState(false);
  const style = {
    position: "absolute",
    top: "50%",
    left: "50%",
    transform: "translate(-50%, -50%)",
    width: 400,
    bgcolor: "background.paper",
    border: "2px solid #000",
    boxShadow: 24,
    p: 4,
  };

  const theme = createTheme({
    palette: {
      primary: {
        main: "#a855f7",
        contrastText: "#475569",
      },
      secondary: {
        contrastText: "#475569",
      }
    },
    typography: {
      fontFamily: "Roboto",
    },
  });

  return (
    <ThemeProvider theme={theme}>
      <div className="App">
        <Box
          sx={{
            width: "100%",
            justifyContent: "space-between",
            flexDirection: "row",
            display: "flex",
            alignItems: "center",
            padding: "0 32px",
            boxSizing: "border-box",
          }}
        >
          <Typography>Finance Tracker</Typography>
          <Typography>Balance: 0TRY</Typography>
        </Box>
        <Box
          sx={{
            display: "grid",
            gridTemplateColumns: "25% 1fr",
            height: "100%",
          }}
        >
          <Box
            sx={{
              width: "25%",
              justifyContent: "space-between",
              flexDirection: "column",
              display: "flex",
              alignItems: "center",
              padding: "8px",
            }}
          >
            <Box
              sx={{
                display: "flex",
                alignItems: "center",
                flexDirection: "column",
                width: "100%",
              }}
            >
              <Box
                sx={{
                  justifyContent: "space-between",
                  display: "flex",
                  width: "100%",
                }}
              >
                <Button onClick={() => setModalOpen(true)}>Income</Button>
                <Button>Expense</Button>
              </Box>
              <Typography>Base Currency: TRY</Typography>
              <Box
                sx={{
                  display: "flex",
                  flexDirection: "row",
                  justifyContent: "space-between",
                  width: "100%",
                }}
              >
                <Typography>Total Incomes: 0TRY</Typography>
                <Typography>Total Expenses: 0TRY</Typography>
              </Box>
            </Box>
            <Typography>Finance Traker</Typography>
          </Box>
          <Box
            sx={{
              backgroundColor: "rgb(248 250 252)",
              borderTop: "1px solid rgb(226 232 240)",
              borderLeft: "1px solid rgb(226 232 240)",
              borderTopLeftRadius: "16px",
              width: "100%",
              height: "100%",
              overflow: "auto",
              boxSizing: "border-box",
            }}
          >
            <Box
              sx={{
                display: "flex",
                justifyContent: "flex-end",
                width: "100%",
                marginTop: "16px",
                flexDirection: "row",
              }}
            >
              Filters:
            </Box>
            <Typography>No Match</Typography>
          </Box>
        </Box>
        <Modal
          open={modalOpen}
          onClose={() => setModalOpen(false)}
          aria-labelledby="modal-modal-title"
          aria-describedby="modal-modal-description"
        >
          <Box sx={style}>
            <Typography id="modal-modal-title" variant="h6" component="h2">
              Text in a modal
            </Typography>
            <Typography id="modal-modal-description" sx={{ mt: 2 }}>
              Duis mollis, est non commodo luctus, nisi erat porttitor ligula.
            </Typography>
          </Box>
        </Modal>
      </div>
    </ThemeProvider>
  );
}

export default App;
