import {
  Autocomplete,
  Box,
  Button,
  Modal,
  TextField,
  Typography,
} from "@mui/material";
import React, { useState } from "react";
import { useAmount } from "../Context/Context";

const Sidebar = () => {
  const { total, baseCurrency, setBaseCurrency, currencies, setCurrencies } =
    useAmount();
  const [modalOpen, setModalOpen] = useState(false);
  const [amount, setAmount] = useState(0);
  const [currency, setCurrency] = useState("USD");
  const [desc, setDesc] = useState("");
  const style = {
    position: "absolute",
    top: "50%",
    left: "50%",
    transform: "translate(-50%, -50%)",
    width: 400,
    bgcolor: "background.paper",
    border: "2px solid #000",
    boxShadow: 24,
    p: 4,
  };
  return (
    <Box sx={{ display: "grid", gridTemplateRows: "1fr auto", p: 2 }}>
      <Box>
        <Box sx={{ display: "flex", gap: 1, mb: 3 }}>
          <Button
            sx={{ flex: 1 }}
            fullWidth
            variant="contained"
            onClick={() => setModalOpen(true)}
          >
            Income
          </Button>
          <Button fullWidth sx={{ flex: 1 }} variant="outlined">
            Expense
          </Button>
        </Box>
        <Box sx={{ display: "flex", gap: 1, alignItems: "center" }}>
          <Typography>Base Currency:</Typography>

          <Autocomplete
            size="small"
            options={Object.keys(currencies)}
            onChange={(event, newValue) => {
              setBaseCurrency(newValue);
            }}
            renderInput={(params) => (
              <TextField {...params} placeholder="Currency" />
            )}
          />
        </Box>
        <Typography>Total Incomes: 0TRY</Typography>
        <Typography>Total Expenses: 0TRY</Typography>
      </Box>
      <Typography
        variant="overline"
        color="primary"
        textAlign="center"
        fontWeight={600}
      >
        Finance Tracker
      </Typography>
      <Modal
        open={modalOpen}
        onClose={() => setModalOpen(false)}
        aria-labelledby="modal-modal-title"
        aria-describedby="modal-modal-description"
      >
        <Box sx={style}>
          <Typography
            id="modal-modal-title"
            alignItems={"center"}
            display={"flex"}
            justifyContent={"center"}
            variant="h6"
            component="h2"
          >
            Ä°ncome
          </Typography>
          <TextField
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
          />
        </Box>
      </Modal>
    </Box>
  );
};
export default Sidebar;
