import logo from "./logo.svg";
import "./App.css";
import {
  Autocomplete,
  Box,
  Button,
  Modal,
  TextField,
  Typography,
} from "@mui/material";
import { useEffect, useState } from "react";
import { ThemeProvider, createTheme } from "@mui/material/styles";
import Balance from "./components/atom/Balance";
import AmountContext, { useAmount } from "./components/Context/Context";
import HomePage from "./pages/HomePage";

function App() {
  const { total, baseCurrency, setBaseCurrency, currencies, setCurrencies } =
    useAmount();
  const options = [
    { label: "None", id: "none" },
    { label: "Income", id: "income" },
    { label: "Expense", id: "expense" },
  ];
  const [filter, setFilter] = useState({
    type: options[0],
    currency: "USD",
  });
  const getCurrency = () => {
    fetch(
      "https://api.freecurrencyapi.com/v1/latest?apikey=fca_live_bauGK9JbiAlk0rZeVwHa8d2A6wq1tqmuwD0dN4VQ"
    )
      .then((res) => res.json())
      .then((res) => {
        setCurrencies(res.data);
      });
  };
  useEffect(() => {
    getCurrency();
  }, []);

  const [modalOpen, setModalOpen] = useState(false);
  const style = {
    position: "absolute",
    top: "50%",
    left: "50%",
    transform: "translate(-50%, -50%)",
    width: 400,
    bgcolor: "background.paper",
    border: "2px solid #000",
    boxShadow: 24,
    p: 4,
  };

  const theme = createTheme({
    palette: {
      primary: {
        main: "#a855f7",
        contrastText: "#ffffff",
      },
    },
    typography: {
      fontFamily: "Roboto",
    },
  });

  return (
    <AmountContext>
      <ThemeProvider theme={theme}>
        <HomePage />
      </ThemeProvider>
    </AmountContext>
  );
}

export default App;
